INSERT INTO beers (name, category, description, image, abv, ibu, "formatId", "breweryId") VALUES ('Spered', 'Pale Ale', 'Petit clin d’œil aux origines finistériennes de Jérôme, cofondateur de la Little Atlantique Brewery, la Spered (“esprit” en breton) est notre blonde classique ou bière de soif.Blonde, légère, à la robe claire et brillante, c’est la première bière que Simon, notre maître-brasseur, a élaborée au sein de notre établissement. Il a souhaité faire de la Spered une bière accessible qui tend vers des saveurs florales et s’enrichit de subtiles notes d’agrumes.', '/uploads/beers/spered.png', 5.0, 30, 1, 1);
INSERT INTO beers (name, category, description, image, abv, ibu, "formatId", "breweryId") VALUES ('Exocet', 'IPA', 'A l’image de ce délicat poisson des mers chaudes, Exocet, à la robe cuivrée, ondule entre l’amertume caractéristique de l''India Pale Ale (IPA) et de légères saveurs d’agrumes et de fruits exotiques, qu’elle survole avec douceur.','/uploads/beers/exocet.png', 6.0, 45, 1, 1);
